import React, { Component } from 'react';
import { DropdownButton, MenuItem } from 'react-bootstrap';
import List from './List';

class FilteredList extends Component {
  constructor(props) {
    super(props);
    // Add a new key/value pair in the state to keep track of type
    this.state = {
      search: "",
      type: "all", // "all" initially to show all produce
    };
  }

  // Sets the state whenever the user types on the search bar
  onSearch = (event) => {
    this.setState({ search: event.target.value.trim().toLowerCase() });
  }

  // Add an event handling method for when an item in the dropdown is selected
  handleTypeSelect = (type) => {
    this.setState({ type });
  }

  // Modify filterItem to consider both search and type filters
  filterItem = (item) => {
    return (
      item.name.toLowerCase().search(this.state.search) !== -1 &&
      (this.state.type === "all" || item.type.toLowerCase() === this.state.type.toLowerCase())
    );
  }

  render() {
    const produceTypes = ["all", "fruit", "vegetable"]; // Add other types as needed

    return (
      <div className="filter-list">
        <h1>Produce Search</h1>

        {/* Add DropdownButton and MenuItem components */}
        <DropdownButton
          id="typeDropdown"
          title={`Type: ${this.state.type}`}
          onSelect={this.handleTypeSelect}
        >
          {produceTypes.map((type) => (
            <MenuItem key={type} eventKey={type}>
              {type.charAt(0).toUpperCase() + type.slice(1)}
            </MenuItem>
          ))}
        </DropdownButton>

        <input type="text" placeholder="Search" onChange={this.onSearch} />

        {/* Pass the filtered produce to the List component */}
        <List items={this.props.items.filter(this.filterItem)} />
      </div>
    );
  }
}

export default FilteredList;
